<?phpclass Login extends Controller{	public $helpers		= array('Html', 'Form');	public $adminArea	= TRUE;		public function enter()	{		if ( $this->user->isAdmin() )			$this->redirectBackend('Overview', 'start');				if ( $this->form->isSubmitted('form_login') )		{			$username	= $this->form->getValue('username');			$password	= $this->form->getValue('password');						$this->user->login($username, $password);						if ( !$this->user->isAdmin() )			{				$this->form->setError('notice', 'Wrong username of password');			}			if ( $this->form->isValid('form_login') )			{				$this->redirectBackend('Overview', 'start');			}		}		$this->view('login.tpl.php');	}		public function logout()	{		$this->user->logout();		$this->redirectBackend('Login', 'enter');	}}?>