<?phpclass Overview extends Controller{	public $helpers		= array('Html', 'Form');	public $adminArea	= TRUE;		public function start()	{		if ( !$this->user->isAdmin() )			$this->redirectBackend('Login', 'enter');					$user			= Session::get('user');		$failedLogins	= $this->user->getAllFailedLogins();		$users			= $this->user->getAllUsers();		$pageHits		= $this->model->Visitors->countAll();		$online			= $this->model->Visitors->countOnlineUsers();		$sessCount		= $this->model->Visitors->countUniqueSessVisitors();		$hostCount		= $this->model->Visitors->countUniqueHostVisitors();				$info['last_login']		= $user['last_login'];		$info['last_ip']		= $user['last_ip'];		$info['last_host']		= $user['last_host'];		$info['failed_logins']	= $user['last_failed_login_count'];						$this->set('info', $info);		$this->set('users', $users);		$this->set('pageHits', $pageHits);		$this->set('sessCount', $sessCount[0]['count']);		$this->set('hostCount', $hostCount[0]['count']);		$this->set('online', $online);		$this->set('failedLogins', $failedLogins);				$this->set('headline', 'Start');		$this->view('overview.tpl.php');	}			public function serverInfo()	{		$this->view('server_info.tpl.php');	}}?>