<?phpclass Site extends Controller{	public $helpers = array('Html', 'HtmlTemplate', 'Form');	public function index()	{		$this->view('index.tpl.php');	}		public function error($request = NULL)	{		switch ( $_SERVER['REQUEST_URI'] )		{			case '/robots.txt':				$this->render = false;				$output  = "User-agent: *\n";				$output .= "Allow: /";				return $output;				break;			default:				// ADD TEMPLATE ELEMENTS				$this->htmltemplate->setTitle('404 Fehler - URL not found.');				// VIEW VARIABLES				$this->set('url', $request);				// VIEW OPTIONS				$this->set('headline', 'URL not found.');				$this->view('error.tpl.php');		}	}}?>