<?php echo $form->start('form_message_action', null, array('id' => 'form_message_action')); ?>	<table>		<colgroup>			<col width="20" />			<col width="80" />			<col width="260" />			<col width="100" />			<col />			<col width="20" />		</colgroup>		<thead>			<tr>				<td colspan="6">					<div id="delete" class="msgActionButtons" style="float:right;"						onclick="document.getElementById('delete').innerHTML += '<input type=&quot;hidden&quot; name=&quot;form_message_action[delete]&quot; />';							document.getElementById('form_message_action').submit();">						<?php echo $html->img('/img/packages/user_messages/delete.png', 'Restore'); ?>						<br/>						<span>L&ouml;schen</span>					</div>					<div id="restore" class="msgActionButtons" style="float:right;"						onclick="document.getElementById('restore').innerHTML += '<input type=&quot;hidden&quot; name=&quot;form_message_action[restore]&quot; />';							document.getElementById('form_message_action').submit();">						<?php echo $html->img('/img/packages/user_messages/restore.png', 'Delete permanently'); ?>						<br/>						<span>Restoren</span>					</div>					<div style="clear:both;"></div>					<hr />				</td>			</tr>			<tr>				<th style="text-align:center;">#</th>				<th>Von</th>				<th>Betreff</th>				<th colspan="2">Empfangen</th>				<th style="text-align:center;"></th>			</tr>		</thead>		<tfoot>			<tr>				<th colspan=6><hr/></th>			</tr>			<tr>				<td colspan="6">					<?php echo Html::img('/img/packages/user_messages/flag_msg_new.png', 'unread'); ?> neue Nachricht<br/>					<?php echo Html::img('/img/packages/user_messages/flag_msg_read.png', 'is read'); ?> gelesene Nachricht<br/>					<?php echo Html::img('/img/packages/user_messages/flag_msg_replied.png', 'replied'); ?> beantwortete Nachricht<br/>				</td>			</tr>		</tfoot>		<tbody>			<?php $i=0; ?>			<?php foreach ($messages as $message): ?>				<?php $style = ( !$message['is_read'] ) ? array('style' => 'font-weight: bold;') : NULL; ?>				<?php $user_link	= ($message['fk_from_user_id']) ? Html::l($message['from_username'], $userProfileCtl, $userProfileMethod, array($message['fk_from_user_id'])) : $systemUserName; ?>				<?php $flag			= ( $message['is_read'] ) ? ( ($message['is_answered'])? 'flag_msg_replied.png': 'flag_msg_read.png') : 'flag_msg_new.png'; ?>				<?php $flag_img		= Html::img('/img/packages/user_messages/'.$flag, 'flag'); ?>				<tr>					<td><?php echo $flag_img; ?></td>					<td><?php echo $user_link; ?></td>					<td><?php echo Html::l($message['subject'], $className, 'read', array($message['id'], 'inbox'), $style); ?></td>					<td><?php echo MyTime::getWeekDayShort(strtotime($message['created'])).', '.date("d.m.Y", strtotime($message['created'])); ?></td>					<td><?php echo date('H:i', strtotime($message['created'])); ?></td>					<td style="text-align:center;"><?php echo $form->checkBox('message_id['.($i).']', $message['id']); ?></td>				</tr>				<?php $i++; ?>			<?php endforeach; ?>		</tbody>	</table><?php echo $form->end(); ?>