<?phpclass FacesTable extends Table{	protected $table	= 'moment_faces';	protected $fields	= array(		'id'				=> 'id',		'fk_user_id'		=> 'fk_user_id',		'face_type'			=> 'face_type',		'face_color'		=> 'face_color',		'hair_type'			=> 'hair_type',		'hair_color'		=> 'hair_color',		'eyebrow_type'		=> 'eyebrow_type',		'eyebrow_color'		=> 'eyebrow_color',		'eye_type'			=> 'eye_type',		'eye_color'			=> 'eye_color',		'nose_type'			=> 'nose_type',		'nose_color'		=> 'nose_color',		'mouth_type'		=> 'mouth_type',		'mouth_color'		=> 'mouth_color',		'beard_type'		=> 'beard_type',		'beard_color'		=> 'beard_color',		'hat_type'			=> 'hat_type',		'hat_color'			=> 'hat_color',		'created'			=> 'created',		'modified'			=> 'modified',		'url_params'		=> '(CONCAT(face_type, \'/\', face_color, \'/\', hair_type, \'/\', hair_color, \'/\', eyebrow_type, \'/\', eyebrow_color, \'/\', eye_type, \'/\', eye_color, \'/\', nose_type, \'/\', nose_color, \'/\', mouth_type, \'/\', mouth_color, \'/\', beard_type, \'/\', beard_color, \'/\', hat_type, \'/\', hat_color))',	);	public function getAllByUserId($user_id)	{		$condition = sprintf('`fk_user_id` = %d', $user_id);		return $this->_get(null, $condition, null, array('created' => 'DESC'));	}	public function add($data)	{		return $this->_add($data);	}	public function update($id, $data)	{		return $this->_updateRow($id, $data);	}	public function delete($id)	{		return $this->_deleteRow($id);	}	public function isMyFace($face_id, $user_id)	{		$condition = sprintf('`id` = %d AND `fk_user_id` = %d', $face_id, $user_id);		return $this->_count($condition);	}}?>