<?phpclass ThanksTable extends Table{	protected $table	= 'user_thanks';	protected $fields	= array(		'id'				=> 'id',		'fk_from_user_id'	=> 'fk_from_user_id',		'fk_to_user_id'		=> 'fk_to_user_id',		'created'			=> 'created',		'from_username'		=> '(SELECT username FROM users WHERE users.id = fk_from_user_id)',		'to_username'		=> '(SELECT username FROM users WHERE users.id = fk_to_user_id)',	);	public function countThanks($user_id)	{		$condition = sprintf('`fk_to_user_id` = %d', $user_id);		return $this->_count($condition);	}	public function getThanks($user_id)	{		$condition = sprintf('`fk_to_user_id` = %d', $user_id);		return $this->_get(null, $condition, null, array('CREATED' => 'DESC'));	}	public function getWhoIThanked($my_user_id)	{		$condition = sprintf('`fk_from_user_id` = %d', $my_user_id);		return $this->_get(null, $condition, null, array('CREATED' => 'DESC'));	}	public function thankYou($from_user_id, $to_user_id)	{		$fields	= array(			'fk_from_user_id'	=> $from_user_id,			'fk_to_user_id'		=> $to_user_id,		);		return $this->_add($fields);	}	public function didIAlreadyThankYou($from_user_id, $to_user_id)	{		$condition = sprintf('`fk_from_user_id` = %d AND `fk_to_user_id` = %d', $from_user_id, $to_user_id);		return $this->_count($condition);	}}?>