<?phpabstract Class BackendController extends Controller{	public function __construct()	{		parent::__construct();	}		protected function redirectBackend($controller = NULL, $method = NULL, $params = array())	{		if (is_null($controller))			$controller	= Url::getController();		if (is_null($method))			$method	= Url::getMethod();		$args = '';		if ( is_array($params) )		{			foreach ($params as $param)			{				// need to encode twice because of ? needs to be double encoded				// otherwise mode rewrite will break				$param	= rawurlencode($param);				// double encode / and \				$param	= str_replace(array('%2F','%5C'), array('%252F','%255C'), $param);				$param	= rawurlencode($param);				$args .= $param.DS;			}		}		$link = DS.$GLOBALS['BACKEND_PATH'].DS.$controller.DS.$method.DS.$args;			header('Location: '.$link) ;		}}?>