<?phpabstract Class Block{	/* ***************************************************** VARIABLES ***************************************************** */	/*	* The following will define the Variables and View	*/	private $vars	= array();	// all variables parsed to the view	private $view	= null;		// the view itself to use	/* ***************************************************** CONSTRUCTOR ***************************************************** */	function __construct()	{	}	function __destruct()	{	}	/* ***************************************************** BLOCK SETTER ***************************************************** */	protected function set($var, $value)	{		$this->vars[$var] = $value;	}	protected function view($view)	{		$this->view = $view;	}	/* ***************************************************** INDEX GETTER ***************************************************** */	public function getVars()	{		return $this->vars;	}	public function getView()	{		return $this->view;	}}?>