<?phpabstract Class ControllerModel{	/* ***************************************************** VARIABLES ***************************************************** */	/*	 * Which Tables to autoload into this Model	 */	protected $tables = array();	/*	* Determines whether this controller is a 'package' Controller	* If yes, we have to load the view from a different	* location	*/	protected $package = null;	/* ***************************************************** Constructor ***************************************************** */	public function __construct()	{		// initialize table helpers		foreach ($this->tables as $table)		{			$new_var		= $table;			$this->$new_var = Loader::loadTable($table, $this->package);		}	}}?>