<?phpabstract Class BlockModel{	/* ***************************************************** VARIABLES ***************************************************** */	/*	 * Which Tables to autoload into this Model	 */	protected $tables = array();	/* ***************************************************** Constructor ***************************************************** */	public function __construct()	{		$block = str_replace('BlockModel', '', get_called_class());		// initialize table helpers		foreach ($this->tables as $table)		{			$new_var		= $table;			$this->$new_var = Loader::loadTable($table, $block);		}	}}?>