<?phpclass ContactBlock extends BlockController{	public $helpers = array('Html', 'Form');	/* ***************************************** FORM VALIDATOR ******************************************/	protected $formValidator = array(		// Form for adding contact		'form_add_contact'	=> array(			'name'	=> array(				'minLen'	=> array(					'rule'		=> array('minLen', 1),					'error'		=> '',				),			),			'message'	=> array(				'minLen'	=> array(					'rule'	=> array('minLen', 5),					'error'	=> '',				),			),			'subject'	=> array(				'between'	=> array(					'rule'	=> array('between', array(1,4)),					'error'	=> '',				),			),			'email'	=> array(				'isEmail'	=> array(					'rule'	=> array('isEmail'),					'error'	=> '',				),			),		),	);	/* **********************************************************************************************************************	*	*   F U N C T I O N S	*	* **********************************************************************************************************************/	public function form_addContact($subjects)	{		//------ Override Form validator with appropriate language		$this->formValidator['form_add_contact']['name']['minLen']['error']		= $this->language->form_err_name;		$this->formValidator['form_add_contact']['message']['minLen']['error']	= $this->language->form_err_message;		$this->formValidator['form_add_contact']['subject']['between']['error']	= $this->language->form_err_subject;		$this->formValidator['form_add_contact']['email']['isEmail']['error']	= $this->language->form_err_email;		//------ Build subject Array		$subArr[0]['id']	= 0;		$subArr[0]['value'] = $this->language->form_input_subject;		$size = sizeof($subjects);		for($i=0; $i<$size; $i++)		{			$subArr[$i+1]['id']		= ($i+1);			$subArr[$i+1]['value']	= $subjects[$i];		}		//------ Adjust Subject Form Rule (according to the number of subjects)		$this->formValidator['form_add_contact']['subject']['between']['rule'] = array('between', array(1,$size));		// ------------------------- FORM SUBMITTED AND VALID -------------------------		if ( $this->validateForm('form_add_contact')  )		{			// ------------------------- GET FORM VALUES -------------------------			$name		= $this->form->getValue('name');			$email		= $this->form->getValue('email');			$subject_id	= $this->form->getValue('subject');			$message	= $this->form->getValue('message');			$subject	= $subArr[$subject_id]['value'];			$name		= Strings::removeTags($name);			$email		= Strings::removeTags($email);			$message	= Strings::removeTags($message);			$this->model->Contact->add($this->user->id(), $name, $email, $subject, $message);			return 1;		}		// VIEW VARIABLES		$this->set('language', $this->language);		$this->set('subject', $subArr);		// VIEW OPTIONS		$this->view('contact.tpl.php');		return 0;	}}?>