<?phpclass Contact extends PageController{	protected $helpers = array('HtmlTemplate');	/* **********************************************************************************************************************	*	*   S E T T I N G S	*	* **********************************************************************************************************************/	private	$subjectArr = array('General Inquiry', 'Feedback', 'Report Error');	/* **********************************************************************************************************************	*	*   F U N C T I O N S	*	* **********************************************************************************************************************/	public function index()	{		$submitted = $this->attachBlock('contactForm', 'Contact', 'form_addContact', array($this->subjectArr));		// form was submitted successfully		// show info and the redirect user to the start page		// after 5 seconds delay		if ($submitted)		{			$headline	= 'Thank you for submitting';			$body		= 'Your message has been submitted successfully.';			$this->redirectDelayed($GLOBALS['DEFAULT_CONTROLLER'], $GLOBALS['DEFAULT_METHOD'], array(), $headline, $body, 5);			return;		}		$this->set('active', 'Contact');		$this->view('contact.tpl.php');	}}?>