<?phpclass MomentCommentsTable extends Table{	protected $table	= 'moment_comments';	protected $fields	= array(		'id'				=> 'id',		'fk_user_id'		=> 'fk_user_id',		'fk_moment_id'		=> 'fk_moment_id',		'comment'			=> 'comment',		'created'			=> 'created',		'modified'			=> 'modified',				'from_username'		=> '(SELECT username FROM users WHERE id=fk_user_id)',	);	public function getMomentComments($moment_id)	{		$where = sprintf("`fk_moment_id` = %d", $moment_id);		return $this->_get(NULL, $where, NULL, $order = array('created' => 'ASC'));	}		public function add($user_from_id, $moment_id, $comment)	{		$data['fk_user_id']		= $user_from_id;		$data['fk_moment_id']	= $moment_id;		$data['comment']		= $comment;				return $this->_add($data);	}}?>