<?php /*Fast Core*/ namespace{function debug($arr){echo '<pre>';print_r($arr);echo '</pre>';}function getmicrotime(){return microtime(true);}function ob_error_handler($str){$error=error_get_last();if ($error){return ini_get('error_prepend_string')."\n".'Fatal error: '.$error['message'].' in '.$error['file'].' on line '.$error['line']."\n".ini_get('error_append_string');}return $str;}function custom_php_error_handler($errno,$error,$file,$line,$context){switch ($errno){case E_USER_ERROR:echo '<div style="z-index:555;font-size:11px;font-family:arial;border:solid 1px red;padding:5px;background:gray">';echo'<span style="color:red; font-weight:bold;">Fatal Error ['.$errno.']</span>: <strong>'.$error.'</strong><br/><br/>';echo'<strong>File:</strong> '.$file.'<br/>';echo'<strong>Line:</strong> '.$line.'<br/>';echo'<strong>Context:</strong> '.$context.'<br/>';echo'<strong>PHP:<strong> ' . PHP_VERSION . ' (' . PHP_OS . ')<br/><br/>';echo'EXIT...<br/>';echo '</div>';exit(1);break;case E_USER_WARNING:echo '<div style="z-index:555;font-size:11px; font-family:arial;border:solid 1px #FFA500;padding:5px;background:gray">';echo'<span style="color:#FFA500; font-weight:bold;">Warning ['.$errno.']</span>: <strong>'.$error.'</strong><br/><br/>';echo'<strong>File:</strong> '.$file.'<br/>';echo'<strong>Line:</strong> '.$line.'<br/>';echo'<strong>Context:</strong> '.$context.'<br/>';echo '</div>';break;case E_USER_NOTICE:echo '<div style="z-index:555;font-size:11px; font-family:arial;border:solid 1px #FFA500;padding:5px;background:gray">';echo'<span style="color:#FFA500; font-weight:bold;">Notice ['.$errno.']</span>: <strong>'.$error.'</strong><br/><br/>';echo'<strong>File:</strong> '.$file.'<br/>';echo'<strong>Line:</strong> '.$line.'<br/>';echo'<strong>Context:</strong> '.$context.'<br/>';echo '</div>';break;case E_WARNING:echo '<div style="z-index:555;font-size:11px; font-family:arial;border:solid 1px red;padding:5px;background:gray">';echo'<span style="color:red; font-weight:bold;">Runtime Error ['.$errno.']</span>: <strong>'.$error.'</strong><br/><br/>';echo'<strong>File:</strong> '.$file.'<br/>';echo'<strong>Line:</strong> '.$line.'<br/>';echo'<strong>Context:</strong> '.$context.'<br/>';echo '</div>';break;case E_NOTICE:echo '<div style="z-index:555;font-size:11px; font-family:arial;border:solid 1px #FFA500;padding:5px;background:gray">';echo'<span style="color:#FFA500; font-weight:bold;">Runtime Notice ['.$errno.']</span>: <strong>'.$error.'</strong><br/><br/>';echo'<strong>File:</strong> '.$file.'<br/>';echo'<strong>Line:</strong> '.$line.'<br/>';echo'<strong>Context:</strong> '.$context.'<br/>';echo '</div>';break;default:echo '<div style="z-index:555;font-size:11px; font-family:arial;border:solid 1px red;padding:5px;background:gray">';echo'<span style="color:red; font-weight:bold;">Unknown Error ['.$errno.']</span>: <strong>'.$error.'</strong><br/><br/>';echo'<strong>File:</strong> '.$file.'<br/>';echo'<strong>Line:</strong> '.$line.'<br/>';echo'<strong>Context:</strong> '.$context.'<br/>';echo '</div>';break;}return true;}}namespace Core\Init{class CoreAbstract{protected static $error=null;public static function initialize(){self::$error='initialize() function not defined';return false;}public static function cleanup(){}public static function getError(){return self::$error;}}class CoreSettings extends CoreAbstract{public static $showPhpErrors=false;public static $showSqlErrors=false;public static $showFwErrors=false;public static $logPhpErrors=false;public static $logSqlErrors=false;public static $logFwErrors=false;private static $coreLogFile=null;private static $userLogFile=null;private static $timezone=null;public static function initialize(){self::_activateUTF8Encoding();self::$showPhpErrors=$GLOBALS['SHOW_PHP_ERRORS'];self::$showSqlErrors=$GLOBALS['SHOW_SQL_ERRORS'];self::$showFwErrors=$GLOBALS['SHOW_FRAMEWORK_ERRORS'];self::$logPhpErrors=$GLOBALS['LOG_PHP_ERRORS'];self::$logSqlErrors=$GLOBALS['LOG_SQL_ERRORS'];self::$logFwErrors=$GLO