e(){$short=self::chooseLanguage();$success=self::loadFile($short);return $success;}public static function getLangShort(){if ($GLOBALS['LANGUAGE_ENABLE']==true)return self::$langShort;else return $GLOBALS['HTML_DEFAULT_LANG_SHORT'];}public static function getLangLong(){if ($GLOBALS['LANGUAGE_ENABLE']==true)return self::$langLong;else return $GLOBALS['HTML_DEFAULT_LANG_LONG'];}public static function changeLanguage($short_lang='en'){\SysLog::i('Language','Change to '.$short_lang);\Core\Init\CoreSession::set('language',array('short'=> $short_lang));$short=self::chooseLanguage();self::loadFile($short);}public function __construct($plugin,$type,$controller){$section=(strlen($plugin)) ? 'plugins/'.$plugin : 'usr';$sub=($type=='page') ? 'PageSection' : (($type=='layout') ? 'LayoutSection' : 'BlockSection');$sub=(strlen($plugin)) ? $sub : $sub.'/'.$controller;$path='/root/'.$section.'/'.$sub.'/'.$type;$this->_path=$path;\SysLog::i('Language','Creating instance for: ['.$type.'] in: '.$path);}public function set($function){$this->_id=$function;$path=$this->_path.'[@id="'.$function.'"]';$this->_section=self::$_language->xpath($path);}public function setCore($id){$this->_section=self::$_language->xpath('/root/core/default/page[@id="'.$id.'"]');}public function getCustom($path,$key){$tmp=self::$_language->xpath($path);if (!isset($tmp[0])){\SysLog::e('Language','['.$path.'"]['.$key.'] does not exist');if (\Core\Init\CoreSettings::$showPhpErrors==0)return '';}if (!count($tmp[0]->$key)){\SysLog::e('Language','['.$path.'"]['.$key.'] does not exist');if (\Core\Init\CoreSettings::$showPhpErrors==0)return '';}if (count($tmp[0]->$key) > 1)return (Array)$tmp[0]->$key;else return (String)$tmp[0]->$key;}public function __get($key){if (!isset($this->_section[0])){\SysLog::e('Language','['.$this->_path.' id="'.$this->_id.'"]['.$key.'] does not exist');if (\Core\Init\CoreSettings::$showPhpErrors==0)return '';}if (!count($this->_section[0]->$key)){\SysLog::e('Language','['.$this->_path.' id="'.