,self::$link)){self::$error='Encodings and Connections could not be set to utf8';return false;}return true;}public static function cleanup(){if (is_object(self::$link))mysql_close(self::$link);}public static function select($query){self::$query=$query;$start=microtime(true);$result=mysql_query($query,self::$link);$time=self::_getQueryTime($start,microtime(true));$data=array();if (!$result){\SysLog::sqlError('select',self::_formatError($query,$time));return (-1);}while ($row=mysql_fetch_array($result,MYSQL_ASSOC))$data[]=$row;if (!mysql_free_result($result))\SysLog::sqlError('Cannot Free Result',self::_formatError($query,$time),$data);\SysLog::sqlAppendTime($time);\SysLog::sqlInfo('select',self::_formatError($query,$time),$data);return ($data);}public static function selectNumRows($query){self::$query=$query;$start=microtime(true);$result=mysql_query($query,self::$link);$time=self::_getQueryTime($start,microtime(true));if (!$result){\SysLog::sqlError('numRows',self::_formatError($query,$time));return (-1);}$num=mysql_numrows($result);$time=self::_getQueryTime($start,microtime(true));if (!mysql_free_result($result))\SysLog::sqlError('Cannot Free Result',self::_formatError($query,$time));\SysLog::sqlAppendTime($time);\SysLog::sqlInfo('numRows',self::_formatError($query,$time));return ($num);}public static function fetch($table,$fields=array(),$where=null,$having=null,$order=array(),$limit_num=null,$limit_start=null){$fields=self::_getFields($fields);$where=self::_getWhere($where);$having=self::_getHaving($having);$order=self::_getOrderBy($order);$limit=self::_getLimit($limit_num,$limit_start);$query=sprintf('SELECT %s FROM `%s` %s %s %s %s',$fields,$table,$where,$having,$order,$limit);return self::select($query);}public static function fetchField($table,$field,$where){$where=self::_getWhere($where);$query=sprintf('SELECT `%s` FROM `%s` %s',$field,$table,$where);$data=self::select($query);if (!isset($data[0][$field])){\SysLog::sqlWarn('fetchField','returning empty field