<?phpnamespace Sweany;interface iDBO{	// used to check if the DataBase	const _implemented = true;		public function escape($string, $quote = false);		/* ******************************************** GENERIC SELECT ******************************************** */	public function select($query,  $callback);		/* ******************************************** FETCH ******************************************** */	public function fetchField($table, $field_name, $condition);	public function fetchRowField($table, $field_name, $id);	public function count($table, $condition);	public function countDistinct($table, $field, $condition);		public function rowExists($table, $id);			/* ******************************************** UPDATE ******************************************** */	public function update($table, $fields, $condition, $affected_row_ids = false);	public function updateRow($table, $id, $fields);	public function incrementFields($table, $fields, $condition, $affected_row_ids = false);				/* ******************************************** INSERT ******************************************** */	public function insert($table, $fields, $return_insert_id = false);			/* ******************************************** DELETE ******************************************** */	public function delete($table, $condition, $affected_row_ids = false);	public function deleteRow($table, $id);		/* ******************************************** HELPER ******************************************** */	public function getNowDateTime();	public function getNowTimeStamp();	public function getNowUnixTimeStamp();		public function tableExists($table);	public function getColumnNames($table);	public function getPrimaryKey($table);	}