<?phpnamespace Sweany;interface iDataBase{	// used to check if the DataBase	const _implemented = true;		public function escape($string);		/* ******************************************** GENERIC SELECT ******************************************** */	public function select($query,  $callback);	public function selectNumRows($query);		/* ******************************************** FETCH ******************************************** */	public function fetchRow($table, $id);	public function fetchRows($table, $ids);	public function fetchField($table, $field_name, $condition);	public function fetchRowField($table, $field_name, $id);	public function count($table, $condition);	public static function idExists($table, $id);	public static function fieldExists($table, $field, $value);			/* ******************************************** UPDATE ******************************************** */	public function update($table, $fields, $condition);	public static function incrementField($table, $field, $condition, $return_ids = false);	public static function incrementFields($table, $fields, $condition, $return_ids = false);				/* ******************************************** INSERT ******************************************** */	public function insert($table, $fields, $return_insert_id);			/* ******************************************** DELETE ******************************************** */	public function delete($table, $condition);	public function deleteRow($table, $id);		/* ******************************************** HELPER ******************************************** */	public function getNowDateTime();	public function getNowTimeStamp();	public function getNowUnixTimeStamp();		public function tableExists($table);	public function getColumnNames($table);	public function getPrimaryKey($table);	}