<?phpClass Language{	private $_language = array();	function __construct($blockClassName, $lang)	{		$block			= str_replace('Block', '', $blockClassName);	// get the package folder (blockClass without 'Block' at the end)		$xml_lang		= BLOCK.DS.$block.DS.'langs'.DS.$lang.'.xml';		if ( file_exists($xml_lang) )		{			Log::SetInfo('LANG', 'Block '.$block.' is using lang: '.$lang);			$this->_language = simplexml_load_file($xml_lang);		}		else		{			Log::setError('LANG', 'No such default language file: '.$xml_lang);			Log::show();			exit;		}	}	public function __get($key)	{		return $this->_language->$key;	}}abstract Class BlockController extends BaseController{	/* ***************************************************** VARIABLES ***************************************************** */	/**	 *  This is an overriden variable from the	 *  BaseController and its only function is	 *  to tell the loadModel function, that it should	 *  directly load a block model instead of a normal model	 */	protected $block	= true;	private $blockName	= null;	/**	 * Determines the language key to use	 */	protected $lang	= 'en';	/**	 * Array holding the language Class	 */	protected $language;	//public $render = false;		//protected $language;	/* ***************************************************** CONSTRUCTOR ***************************************************** */	function __construct()	{		parent::__construct();		$this->blockName	= get_called_class();		// setup languages		$this->lang			= $GLOBALS['HTML_DEFAULT_LANG_SHORT'];		$this->language		= new Language($this->blockName, $this->lang);	}	function __destruct()	{		parent::__destruct();	}}?>