<?phpabstract Class PageModel{	/* ***************************************************** VARIABLES ***************************************************** */	/*	 * Which Tables to autoload into this Model	 */	protected $tables = array();	/* ***************************************************** Constructor ***************************************************** */	public function __construct()	{		// initialize table helpers		foreach ($this->tables as $table)		{			$new_var		= $table;			$this->$new_var = Loader::loadTable($table);		}	}}?>