<?phpclass FrameworkDefault extends PageController{	public $helpers = array('Html', 'HtmlTemplate', 'Form');		public function url_not_found($request = null)	{		switch ( $_SERVER['REQUEST_URI'] )		{			case '/robots.txt':				$this->render = false;				$output  = "User-agent: *\n";				$output .= "Allow: /";				return $output;				break;			default:				// VIEW VARIABLES				$this->set('url', $request);				// VIEW OPTIONS				$this->view('url_not_found.tpl.php');		}	}	public function info_message()	{		if ( !Session::exists('info_message_data') )		{			$this->redirectHome();			return;		}		$info	= Session::get('info_message_data');		$url	= $info['url'];		$delay	= $info['delay'];		$type	= $info['type'];				if ($type= 'view')		{			$view	= $info['view'];			$vars	= $info['vars'];			$this->set('vars', $vars);			$this->view($view);		}		else if ($type='info')		{			$this->set('head', $info['head']);			$this->set('body', $info['body']);			$this->view('info_message.tpl.php');		}		Session::del('info_message_data');		$this->htmltemplate->setRedirect($url, $delay);				$this->set('url', $url);		$this->set('delay', $delay);	}}?>