<?phpfunction debug($arr){	echo '<pre>';	print_r($arr);	echo '</pre>';}function convert_url_umlauts($url_link){	$clean = str_replace('ä', 'ae', $url_link);	$clean = str_replace('ö', 'oe', $clean);	$clean = str_replace('ü', 'ue', $clean);	$clean = str_replace('Ä', 'Ae', $clean);	$clean = str_replace('Ö', 'Oe', $clean);	$clean = str_replace('Ü', 'ue', $clean);	$clean = str_replace('ß', 'ss', $clean);		return $clean;}function clean_url_param($url_link){	$clean = preg_replace("/[^a-zA-Z0-9.\/_|+ -]/", '', $url_link);	$clean = preg_replace("/[\/|+ -]+/", '-', $clean);	return ($clean);}function get_timezone_difference($time1, $time2){	$time1 = strtotime("1980-01-01 $time1");	$time2 = strtotime("1980-01-01 $time2");	if ($time2 < $time1)	{		$time2 += 86400;	}	$diff	= $time2 - $time1;	if ( abs($diff) > (3600*12) )	{		$time 	= (3600*24)-$diff;		$op		= '+';	}	else	{		$time 	= $diff;		$op		= '-';	}	$timestamp	= strtotime("1980-01-01 00:00:00") + ($time);	$offset		= $op.date("H:i", $timestamp);	return $offset;}function returnDate($querydate){$minusdate = date('ymdHi') - $querydate;if($minusdate > 88697640 && $minusdate < 100000000){    $minusdate = $minusdate - 88697640;}    switch ($minusdate) {        case ($minusdate < 99):                    if($minusdate == 1){                        $date_string = '1 minute ago';                    }                    elseif($minusdate > 59){                        $date_string =  ($minusdate - 40).' minutes ago';                    }                    elseif($minusdate > 1 && $minusdate < 59){                        $date_string = $minusdate.' minutes ago';                    }        break;        case ($minusdate > 99 && $minusdate < 2359):                    $flr = floor($minusdate * .01);                    if($flr == 1){                        $date_string = '1 hour ago';                    }                    else{                        $date_string =  $flr.' hours ago';                    }        break;        case ($minusdate > 2359 && $minusdate < 310000):                    $flr = floor($minusdate * .0001);                    if($flr == 1){                        $date_string = '1 day ago';                    }                    else{                        $date_string =  $flr.' days ago';                    }        break;        case ($minusdate > 310001 && $minusdate < 12320000):                    $flr = floor($minusdate * .000001);                    if($flr == 1){                        $date_string = "1 month ago";                    }                    else{                        $date_string =  $flr.' months ago';                    }        break;        case ($minusdate > 100000000):                $flr = floor($minusdate * .00000001);                if($flr == 1){                        $date_string = '1 year ago.';                }                else{                        $date_string = $flr.' years ago';                }        }    return $date_string;}function time_since_now($timestamp){	$diffu = array(		'seconds'	=> 2,		'minutes'	=> 120,		'hours'		=> 7200,		'days'		=> 172800,		'months'	=> 5259487,		'years'		=> 63113851	);	$diff	= time() - strtotime($timestamp, time());	$dt		= '0 seconds ago';	foreach ($diffu as $u => $n)	{		if($diff>$n)		{			$dt = floor($diff/(.5*$n)).' '.$u.' ago';		}	}	return $dt;}function print_arr($arr, $offset, $return = FALSE){	if ( isset($arr[$offset]) )	{		if ($return)			return $arr[$offset];		else			echo $arr[$offset];	}}function getmicrotime(){	list($usec, $sec) = explode(' ',microtime());	return ((float)$usec + (float)$sec);}function array_implode( $glue, $separator, $array ){	if ( ! is_array( $array ) )		return $array;	$string = array();	foreach ( $array as $key => $val )	{		if ( is_array( $val ) )			$val = implode( ',', $val );		$string[] = "{$key}{$glue}{$val}";	}	return implode( $separator, $string );}?>