<?phpclass Site extends PageController{	public function index()	{		// Attaching a block to this view		// see index.tpl.php for how to display the block		$this->attachBlock('forumThreads', 'Forums', 'Forum', 'ActivityOverview', array(6));		// Loading additional css file used for the forum block		// This can however also be done in the block itself.		// Just for demonstration purposes		Css::addFile('/css/forum.css');		// specify the view to be used for this function		$this->view('index.tpl.php');		// If not specified, the default layout will be used		// the default layout view can also be modified, but then there		// will be no layout controller available.		// default view is usr/layout/view/template.tpl.php		// The view for Layouts->FrontPage is specified		// in Layouts Controller in the function 'FrontPage'		// This function loads the login/logout block		$this->layout('Layouts', 'FrontPage');		$this->set('language', $this->language);		// LogHelper Test		LogCat::i('some info text for the log file');		LogCat::w('error occured here');	}}?>