<?phpclass UsersTable extends Table{	public $table	= 'users';	public $alias	= 'User';	public $fields = array(		'id',		'username',		'password',		'password_salt',		'email',		'has_accepted_terms',		'is_admin',		'is_enabled',		'is_deleted',		'is_locked',		'is_fake',		'validation_key',		'reset_password_key',		'session_id',		'last_ip',		'last_host',		'last_login',		'last_failed_login_count',		'created',		'modified'	);	protected $hasCreated	= array(		'created'		=> 'timestamp',	);	protected $hasModified	= array(		'modified'		=> 'timestamp',	);	public $hasMany = array(		'Thread'	=> array(			'table'			=> 'forum_threads',			'fields'		=> array('id', 'title'),			'foreignKey'	=> 'fk_user_id',		),	);}